df.roles <- df.useCP %>% 
  select(Q5.20, grep("Q5.22", names(df.useCP)), grep("Q5.17", names(df.useCP)), grep("Q5.18", names(df.useCP))) %>% 
  select(-Q5.22_3_TEXT, -Q5.17_6_TEXT, -Q5.18_4_TEXT, -Q5.18_4_TEXT...Topics) %>% 
  lapply(FUN = function(x) recode(x,
      "'Lead / Primary' = 4;
      'Equal to others' = 3;
      'Supporting' = 2;
      'Minimal' = 1;
      'None' = 0;
      'The playlist belongs to me.' = 1;
      'The playlist belongs to me and my collaborator(s).' = 1;
      'The playlist belongs to my collaborator(s).' = 0;
      'Yes' = 1;
      'Other' = 0;
      'No' = 0;")) %>% 
  lapply(as.character) %>% 
  lapply(as.numeric)
lm(Q5.22 ~ Q5.17_1 + Q5.17_2 + Q5.17_3 + Q5.17_4 + Q5.17_5 + Q5.17_6, data = df.roles) %>% summary()
