df.musicFreq_2 <- df.raw %>% select(Q2.3, grep("Q7.6", names(df.raw))) %>% select(-Q7.6_7)
df.musicFreq_2 <- lapply(df.musicFreq_2,
                         FUN = function(x) recode(x,
                              "'Never' = 0;
                              'A few times per year or less' = 1;
                              'At least once per month' = 2;
                              'At least once per week' = 3;
                              'At least once per day' = 4;
                              'Yes' = 1;
                              'No' = 0;"))

df.musicFreq_2 <- df.musicFreq_2 %>% 
  lapply(as.character) %>% 
  lapply(as.numeric)
str(df.musicFreq_2)
